var azelString = String(currentAz) + ".000000\n"  + String(currentEl) + ".000000\n";
//var azelString = String(currentAz) + ".000000 " + String(currentEl) + ".000000\n";
//var azelString = String(currentAz) + "0.000000 " + String(currentEl) + "0.000000\n";

msg.payload = azelString;
return msg;

// Parse P <azimuth> <elevation> command into azPart and elPart
if (msg.payload && typeof msg.payload === 'string') {
    const parts = msg.payload.trim().split(/\s+/);
    if (parts.length >= 2 && parts[0] === 'P') {
        // Extract and convert azimuth and elevation to numbers
        msg.azPart = parseFloat(parts[1]); // Azimuth from second part
        msg.elPart = parseFloat(parts[2]); // Elevation from third part
        return msg;
    } else {
        msg.payload = "Error: Invalid P command format";
        return msg;
    }
} else {
    msg.payload = "Error: Payload is not a string";
    return msg;
}
